package in.co.rays.Stringjava;

public class stringpatternjava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name = "Harshit Singh Panchal sir";
		System.out.println("Stringlength : " + name.length());
		System.out.println("6 character : " + name.charAt(6));
		System.out.println("Lowercase : " + name.toLowerCase());
		System.out.println("UpperCase :" + name.toUpperCase());
		System.out.println("Harshit : " + name.startsWith("harshit"));
		System.out.println("chal : " + name.endsWith("chal"));
		System.out.println("Substring : " + name.substring(6));

		

	}

}
